"use strict";function getData(){fetch("https://api.punkapi.com/v2/beers/random").then(function(t){return t.json()}).then(function(t){var e="";t.forEach(function(t){e+="<div><h3>"+t.name+"</h3>\n                  <p>"+t.description+'</p>\n                  <img class="beer" src="'+t.image_url+'" alt="" /></div>'}),document.getElementById("output").innerHTML=e})}document.getElementById("getData").addEventListener("click",getData);